package aula_juliana;

public class at9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Esse sistema vai gerar a tabuada de 8: ");
		
		int num = 8;
		
		for ( int i = 1; i <= 10; i++) {
			int resultado = num * i;
			System.out.println(num + " * " + i + " = " + resultado);
			
		}
		
	}

}
